import React, { useState } from 'react';
import { addMonths, format, subMonths } from 'date-fns';
import { isSameMonth, isSameDay, addDays, parse } from 'date-fns';
import styles from "./ReservationCalendar.module.css"
import _ from 'lodash';

function LoofDiv(currentMonth, selectedDate, OnDateClik) {
    const array = [];
    const dayArray = ['Sun','Mon','The','Wed','Thu','Fri','Sat']
    for (let i = 0; i < 6; i++) {
        for (let j = 0; j < 7; j++) {
            if (0 === i) {
                array.push(<div className={styles.fontStyle}>{dayArray[j]}</div>);
            }
            else {
                array.push(<div>{(i - 1)*7 + j + 1}</div>);
            }
        }

    }

    return array;
}

const ReservationCalendar = () => {

    const [currentMonth, setCurrentMonth] = useState(new Date());

    const [selectedDate, setSelectedDate] = useState(new Date());

    const prevMonth = () =>
    {
        setCurrentMonth(subMonths(currentMonth,1));
    }
    const nextMonth =() => 
    {
        setCurrentMonth(subMonths(currentMonth,1));
    }
    const OnDateClik = (day) => {
        setSelectedDate(day);
    }

    return (
        <div className={styles.containerWrapper}>
            <div className={styles.topCalendar}> 
                <div className={styles.topLeft}>
                    <button type='button' >Left</button>
                    <button type='button' >Right</button>
                </div>
                <div className={styles.topMiddle}>
                </div>
                <div className={styles.topRight}>

                </div>
            </div>
            <div className={styles.container}>
                {LoofDiv(currentMonth,selectedDate,OnDateClik)}
            </div>
        </div>
    )
}

export default ReservationCalendar;
