import React, { Component } from "react";
import Heart from "./Heart";
import Table from './Table';
class MoviesTable extends Component {

  render() {
//table data들을 분류해줄 분류 목록들
    const columns = [
//제목
      {path:"title", name:"Title"},
//장르
      {path:"genre.name", name:"Genre"},
//재고
      {path:"numberInStock", name:"Stock"},
//평점
      {path:"dailyRentalRate", name:"Rate"},
//좋아요, 좋아요 컴포넌트 자체를 객체로 넣어놨다.
      {key:"heart", content: data =><Heart liked={data.like} onClick={this.props.onLike} data={data} /> },
//삭제버튼, 삭제 버튼 자체를 객체로 넣어놓음
      {key:"delete", content: data => (<button
      onClick={() => this.props.onDelete(data)}
      className="btn btn-danger"
    >
      Delete
      </button>) }

  ]
    const { data, onSort, sortColumn } = this.props;
    return (
      <Table data={data} onSort={onSort} sortColumn={sortColumn} columns={columns}/>
    );
  }
}

export default MoviesTable;
